INSERT INTO pluggable_task (id, entity_id, type_id, processing_order, optlock) select (select max(p.id)+1 from pluggable_task p) , e1.id, (select max(p.id) from pluggable_task_type p), 4, 1 from entity e1 where e1.id in (select min(id) from entity);
INSERT INTO pluggable_task_parameter (id, task_id, name, str_value, optlock) select (select max(pp.id)+1 from pluggable_task_parameter pp) , (select max(pt.id) from pluggable_task pt), 'folder.1', '${base_dir}/reports/assets', 1 from entity e1 where e1.id in (select min(id) from entity);
